<script>
export default {
    data() {
        return {
            head: 'https://cube.elemecdn.com/0/88/03b0d39583f48206768a7534e55bcpng.png',
            isShow: {
                backstage: true
            }
        }
    },
    props: {
        accountName: String
    },
    methods: {
        skip(url) {
            if (url == 'exit') this.exit()
            else
                this.$router.push(url)
        },
        exit() {
            console.log(this)
             this.$parent.isLogin()
            this.$axios('logout/', {
                method: 'put',
            }).then(res => {
                console.log(res);
                this.$cookies.remove('activeUser')
                localStorage.removeItem('HEADER_TOKEN');
                this.$notify({
                    title: '退出登录',
                    message: '成功',
                    type: 'success',
                    duration: 2000,
                    showClose: false
                });
                this.$router.push('/');
            }).catch(err => {
                console.log(err);
            })
        }
    }

}
</script>
